Window.OnRender:
      if ${UIElement[EQ2 Bot].Minimized}
      {
	      switch ${Time.Second}
	      {
		      case 1
		      	if ${Script[EQ2Bot].Variable[KillTarget]} > 0
		      	{
				      UIElement[Title@TitleBar@EQ2 Bot]:SetText[Target: ${Actor[${Script[EQ2Bot].Variable[KillTarget]}].Name}]
				    }
			      break
		      case 25
			      UIElement[Title@TitleBar@EQ2 Bot]:SetText[Runtime: ${Math.Calc[(${Time.Timestamp}-${LavishSettings[EQ2Bot].FindSet[Character].FindSet[Temporary Settings].FindSetting[StartTime]})/60]}]
			      break
		      case 45
				    UIElement[Title@TitleBar@EQ2 Bot]:SetText[EQ2Bot - ${Me.Name}]
			      break
		    }
		  }
      else
      {
      	UIElement[Title@TitleBar@EQ2 Bot]:SetText[EQ2Bot - ${Me.Name}]
      	if ${UIElement[EQ2 Bot].FindChild[EQ2Bot Tabs].FindChild[Main].Visible}
      	{
      		if ${UIElement[EQ2 Bot].Height} != 350
      		{
      			UIElement[EQ2 Bot]:SetHeight[350]
      		}
      	}
      	elseif  ${UIElement[EQ2 Bot].FindChild[EQ2Bot Tabs].FindChild[EQ2Bot Options].Visible}
      	{
      		if ${UIElement[EQ2 Bot].Height} != 350
      		{
      			UIElement[EQ2 Bot]:SetHeight[350]
      		}
    		}
      }

combobox.OnSelect:
if ${UIElement[EQ2 Bot].FindUsableChild[MainTank,checkbox].Checked}
{
LavishSettings[EQ2Bot].FindSet[Character].FindSet[General Settings]:AddSetting[I am the Main Tank?,TRUE]
Script[EQ2Bot].Variable[MainTank]:Set[TRUE]
}
